import {
  Flex,
  Box,
  FormControl,
  FormLabel,
  Input,
  InputGroup,
  HStack,
  InputRightElement,
  Stack,
  Button,
  Heading,
  Text,
  useColorModeValue,
  useToast,
} from "@chakra-ui/react";
import { Image } from "@chakra-ui/react";
import React, { useState } from "react";
import { ViewIcon, ViewOffIcon } from "@chakra-ui/icons";
import { useNavigate } from "react-router-dom";
import { useEffect } from "react";

const initial = {
  first_name: "",
  sur_name: "",
  mobile_number: "",
  email: "",
  password: "",
};
const Signup = () => {
  let [formInputObj, setformInputObj] = useState(initial);

  let Navigate = useNavigate();
  const toast = useToast();
  const [showPassword, setShowPassword] = useState(false);
  const [user, setUser] = useState([]);

  const getAdd = async (formInputObj) => {
    let res = await fetch(`https://server-jrrq.onrender.com/users`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formInputObj),
    });
    let data = await res.json();
    setformInputObj(data);
  };

  const get = async () => {
    let res = await fetch(`https://server-jrrq.onrender.com/users`);
    let data = await res.json();
    console.log(data);
    setUser(data);
  };

  useEffect(() => {
    get();
  }, []);

  const handleChange = (e) => {
    console.log(e.target.value, e.target.name);

    setformInputObj({ ...formInputObj, [e.target.name]: e.target.value });
  };

  const handleSignup = (e) => {
    e.preventDefault();
    setformInputObj(initial);
    for (let key in formInputObj) {
      if (formInputObj[key] === "") {
        toast({
          title: `${key}  field is Empty  `,
          description: " Please fill all the information ",
          status: "error",
          duration: 5000,
          isClosable: true,
          position: "top",
        });
        return;
      }
    }

    let em = [];
    let mo = [];
    for (let i = 0; i < user.length; i++) {
      em.push(user[i].email);
      mo.push(user[i].mobile_number);
    }

    if (
      em.includes(formInputObj.email) ||
      mo.includes(formInputObj.mobile_number)
    ) {
      alert("user already exits");
      return Navigate("/login");
    }

    getAdd(formInputObj);
    setTimeout(() => {
      Navigate("/login");
    }, 3000);
  };

  return (
    <Flex
      minH={"100vh"}
      align={"center"}
      justify={"center"}
      bg={useColorModeValue("gray.50", "gray.800")}>
      <Stack spacing={8} mx={"auto"} maxW={"lg"} py={12} px={6}>
        <Box
          rounded={"lg"}
          bg={useColorModeValue("white", "gray.700")}
          boxShadow={"lg"}
          p={8}>
          <Stack spacing={4}>
            <Heading fontSize={"4xl"} textAlign={"center"} color="black">
              Welcome to Signup
            </Heading>

            <Stack direction={["column", "row"]} spacing={6} display="flex" justifyContent={"space-between"} alignItems="center">
              <Stack direction="row" marginLeft={2} gap={10} >
                <Image
                  boxSize="60px"
                  w={"40"}
                  objectFit="cover"
                  src="https://logodownload.org/wp-content/uploads/2014/09/facebook-logo-2.png"
                  alt="Dan Abramov"
                />
                <Image
                  boxSize="60px"
                  w={"40"}
                  objectFit="cover"
                  src="data:image/jpeg;base64,"
                  alt="Dan Abramov"
                />
              </Stack>
            </Stack>
            <HStack>
              <Box>
                <FormControl id="firstName" isRequired>
                  <FormLabel>First Name</FormLabel>
                  <Input
                    onChange={handleChange}
                    type="text"
                    placeholder="First name"
                    name="first_name"
                    value={formInputObj.first_name}
                  />
                </FormControl>
              </Box>
              <Box>
                <FormControl id="lastName">
                  <FormLabel>Last Name</FormLabel>
                  <Input
                    onChange={handleChange}
                    name="sur_name"
                    type="text"
                    placeholder="last name"
                    value={formInputObj.sur_name}
                  />
                </FormControl>
              </Box>
            </HStack>
            <FormControl id="email" isRequired>
              <FormLabel>Email address</FormLabel>
              <Input
                onChange={handleChange}
                name="email"
                type="text"
                placeholder="email"
                value={formInputObj.email}
              />
            </FormControl>
            <FormControl id="mobile" isRequired>
              <FormLabel>Mobile</FormLabel>
              <Input
                onChange={handleChange}
                name="mobile_number"
                type="text"
                placeholder="Mobile number"
                value={formInputObj.mobile_number}
              />
            </FormControl>
            <FormControl id="password" isRequired>
              <FormLabel>Password</FormLabel>
              <InputGroup>
                <Input
                  onChange={handleChange}
                  name="password"
                  type="password"
                  value={formInputObj.password}
                />
                <InputRightElement h={"full"}>
                  <Button
                    variant={"ghost"}
                    onClick={() =>
                      setShowPassword((showPassword) => !showPassword)
                    }>
                    {showPassword ? <ViewIcon /> : <ViewOffIcon />}
                  </Button>
                </InputRightElement>
              </InputGroup>
            </FormControl>
            <Stack spacing={10} pt={2}>
              <Button
                loadingText="Submitting"
                size="lg"
                bg={"yellow.400"}
                color={"white"}
                _hover={{
                  bg: "yellow.500",
                }}
                type="button"
                onClick={handleSignup}>
                Sign up
              </Button>
            </Stack>
            <Stack pt={6}>
              <Text align={"center"}>
                Already a user?{" "}
                <Button onClick={()=>{Navigate("/login")}}>Login</Button>
              </Text>
            </Stack>
          </Stack>
        </Box>
      </Stack>
    </Flex>
  );
};

export default Signup;
